package com.kinginsai.CRM.intercepertor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;@Configurationpublic class WebConfig implements WebMvcConfigurer {    @Autowired    private LoginInterceptor loginInterceptor;    /**     * addPathPatterns 添加拦截的请求， /** 拦截所有请求(包括静态资源)     * excludePathPatterns 放行哪些请求(不拦截)     * @param registry     */    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(loginInterceptor)                .addPathPatterns("/**")                .excludePathPatterns("/login","/registry","/verifyRegistry","/verify","/resources/**");    }}